import React, { useState, useEffect } from 'react';
import characters from './data/characters.json';
import './App.css';

function App() {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [responses, setResponses] = useState({});
  const [showResults, setShowResults] = useState(false);
  const [isLoading, setIsLoading] = useState(true);
  const [gameState, setGameState] = useState('landing'); // 'landing', 'game', 'database', 'results'
  // Preload images for smooth transitions
  useEffect(() => {
    const loadImages = async () => {
      // Only preload images that exist locally (have /images/ path)
      const localImagePromises = characters
        .filter(character => character.imageUrl.startsWith('/images/'))
        .map(character => {
          return new Promise((resolve) => {
            const img = new Image();
            img.onload = () => resolve({ id: character.id, img });
            img.onerror = () => resolve({ id: character.id, img: null });
            img.src = character.imageUrl;
          });
        });

      await Promise.all(localImagePromises);
      setIsLoading(false);
    };

    loadImages();
  }, []);

  const handleResponse = (response) => {
    const currentCharacter = characters[currentIndex];
    setResponses(prev => ({
      ...prev,
      [currentCharacter.id]: response
    }));

    // Add a small delay for smooth transition
    setTimeout(() => {
      if (currentIndex < characters.length - 1) {
        setCurrentIndex(prev => prev + 1);
      } else {
        setShowResults(true);
        setGameState('results');
      }
    }, 300);
  };

  const resetGame = () => {
    setCurrentIndex(0);
    setResponses({});
    setShowResults(false);
    setGameState('landing');
  };

  const getRarityColor = (rarity) => {
    switch (rarity) {
      case 'common': return 'text-gray-400';
      case 'rare': return 'text-blue-400';
      case 'epic': return 'text-purple-400';
      case 'legendary': return 'text-orange-400';
      case 'champion': return 'text-yellow-400';
      default: return 'text-gray-400';
    }
  };

  const getElixirColor = (elixir) => {
    if (elixir <= 2) return 'bg-green-500';
    if (elixir <= 4) return 'bg-yellow-500';
    if (elixir <= 6) return 'bg-orange-500';
    return 'bg-red-500';
  };

  if (isLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-purple-600 to-blue-600">
        <div className="text-center">
          <div className="animate-spin rounded-full h-16 w-16 border-b-2 border-white mx-auto mb-4"></div>
          <h2 className="text-white text-xl font-bold">Loading Clash Royale Characters...</h2>
        </div>
      </div>
    );
  }

  // Landing Page
  if (gameState === 'landing') {
    return (
      <div className="min-h-screen bg-gradient-to-br from-indigo-900 via-purple-900 to-pink-900 relative overflow-hidden">
        {/* Animated Background Elements */}
        <div className="absolute inset-0">
          <div className="absolute top-20 left-10 w-72 h-72 bg-purple-500 rounded-full mix-blend-multiply filter blur-xl opacity-20 animate-pulse"></div>
          <div className="absolute top-40 right-10 w-72 h-72 bg-pink-500 rounded-full mix-blend-multiply filter blur-xl opacity-20 animate-pulse"></div>
          <div className="absolute -bottom-8 left-20 w-72 h-72 bg-indigo-500 rounded-full mix-blend-multiply filter blur-xl opacity-20 animate-pulse"></div>
        </div>

        <div className="relative z-10 p-4">
          <div className="max-w-6xl mx-auto">
            <div className="text-center mb-16 animate-fade-in">
              <div className="mb-8">
                <div className="text-8xl mb-4 animate-bounce">⚡</div>
                <h1 className="text-5xl md:text-7xl font-black text-transparent bg-clip-text bg-gradient-to-r from-yellow-400 via-orange-500 to-red-500 mb-4">
                  SMASH OR PASS
                </h1>
                <h2 className="text-2xl md:text-4xl font-bold text-white mb-8 drop-shadow-lg">
                  CLASH ROYALE EDITION
                </h2>
              </div>
              <p className="text-white text-xl mb-12 max-w-3xl mx-auto leading-relaxed">
                Rate your favorite Clash Royale characters with style! 
                <span className="block text-yellow-300 font-bold">Tap SMASH if you like them, PASS if you don't!</span>
              </p>
            </div>

            <div className="grid md:grid-cols-2 gap-8 max-w-5xl mx-auto">
              {/* Start Game Button */}
              <div className="group relative">
                <div className="absolute -inset-1 bg-gradient-to-r from-green-600 to-blue-600 rounded-3xl blur opacity-75 group-hover:opacity-100 transition duration-1000 group-hover:duration-200 animate-pulse"></div>
                <div className="relative bg-black rounded-3xl p-8 border border-gray-800">
                  <div className="text-center">
                    <div className="text-7xl mb-6 animate-pulse">🎮</div>
                    <h3 className="text-3xl font-black text-white mb-4">START GAME</h3>
                    <p className="text-gray-300 mb-8 text-lg">
                      Begin your epic Clash Royale character rating adventure!
                    </p>
                    <button
                      onClick={() => setGameState('game')}
                      className="bg-gradient-to-r from-green-500 to-blue-500 hover:from-green-600 hover:to-blue-600 text-white font-black py-6 px-12 rounded-2xl text-2xl transition-all duration-300 transform hover:scale-110 hover:shadow-2xl shadow-lg"
                    >
                      🚀 START PLAYING
                    </button>
                  </div>
                </div>
              </div>

              {/* View Database Button */}
              <div className="group relative">
                <div className="absolute -inset-1 bg-gradient-to-r from-purple-600 to-pink-600 rounded-3xl blur opacity-75 group-hover:opacity-100 transition duration-1000 group-hover:duration-200 animate-pulse"></div>
                <div className="relative bg-black rounded-3xl p-8 border border-gray-800">
                  <div className="text-center">
                    <div className="text-7xl mb-6 animate-pulse">📊</div>
                    <h3 className="text-3xl font-black text-white mb-4">VIEW DATABASE</h3>
                    <p className="text-gray-300 mb-8 text-lg">
                      Browse all available Clash Royale characters and their images.
                    </p>
                    <button
                      onClick={() => setGameState('database')}
                      className="bg-gradient-to-r from-purple-500 to-pink-500 hover:from-purple-600 hover:to-pink-600 text-white font-black py-6 px-12 rounded-2xl text-2xl transition-all duration-300 transform hover:scale-110 hover:shadow-2xl shadow-lg"
                    >
                      👀 VIEW CHARACTERS
                    </button>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }

  // Database View
  if (gameState === 'database') {
    return (
      <div className="min-h-screen bg-gradient-to-br from-purple-600 to-blue-600 p-4">
        <div className="max-w-6xl mx-auto">
          <div className="text-center mb-8 animate-fade-in">
            <h1 className="text-4xl md:text-5xl font-bold text-white mb-4">
              Clash Royale Character Database
            </h1>
            <p className="text-white text-lg mb-6">
              All available characters and their images
            </p>
            <button
              onClick={() => setGameState('landing')}
              className="bg-white text-purple-600 px-6 py-3 rounded-full font-bold text-lg hover:bg-gray-100 transition-colors duration-200"
            >
              ← Back to Menu
            </button>
          </div>

          <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 xl:grid-cols-5 gap-4">
            {characters.map((character, index) => (
              <div key={character.id} className="bg-white rounded-2xl p-4 shadow-lg animate-fade-in">
                <div className="text-center">
                  <div className="relative mb-3">
                    <img
                      src={character.imageUrl}
                      alt={character.name}
                      className="w-24 h-24 md:w-32 md:h-32 mx-auto rounded-xl object-cover shadow-md"
                      onError={(e) => {
                        e.target.style.display = 'none';
                        e.target.nextSibling.style.display = 'flex';
                      }}
                    />
                    <div 
                      className="w-24 h-24 md:w-32 md:h-32 mx-auto rounded-xl bg-gray-200 flex items-center justify-center text-gray-500 text-sm font-semibold"
                      style={{ display: 'none' }}
                    >
                      {character.name}
                    </div>

                    {/* Rarity Badge */}
                    <div className={`absolute top-1 right-1 px-2 py-1 rounded-full text-xs font-bold text-white ${getRarityColor(character.rarity)}`}>
                      {character.rarity.toUpperCase()}
                    </div>

                    {/* Elixir Cost */}
                    <div className={`absolute top-1 left-1 w-6 h-6 rounded-full flex items-center justify-center text-white font-bold text-xs ${getElixirColor(character.elixir)}`}>
                      {character.elixir}
                    </div>
                  </div>

                  <h3 className="font-bold text-gray-800 text-sm mb-1">{character.name}</h3>
                  <p className="text-gray-600 text-xs mb-2">{character.description}</p>
                  <div className="text-xs text-gray-500">
                    {character.rarity} • {character.elixir} elixir
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    );
  }

  if (showResults) {
    const smashList = characters.filter(char => responses[char.id] === 'smash');
    const passList = characters.filter(char => responses[char.id] === 'pass');

    return (
      <div className="min-h-screen bg-gradient-to-br from-purple-600 to-blue-600 p-4">
        <div className="max-w-4xl mx-auto">
          <div className="text-center mb-8 animate-fade-in">
            <h1 className="text-4xl md:text-6xl font-bold text-white mb-4">
              Your Results! 🏆
            </h1>
            <div className="space-x-4">
              <button
                onClick={resetGame}
                className="bg-white text-purple-600 px-6 py-3 rounded-full font-bold text-lg hover:bg-gray-100 transition-colors duration-200"
              >
                Play Again
              </button>
              <button
                onClick={() => setGameState('landing')}
                className="bg-gray-500 text-white px-6 py-3 rounded-full font-bold text-lg hover:bg-gray-600 transition-colors duration-200"
              >
                Back to Menu
              </button>
            </div>
          </div>

          <div className="grid md:grid-cols-2 gap-8">
            {/* Smash List */}
            <div className="bg-white rounded-2xl p-6 shadow-2xl animate-slide-in">
              <h2 className="text-2xl font-bold text-green-600 mb-4 flex items-center">
                <span className="text-3xl mr-2">💚</span>
                Smash ({smashList.length})
              </h2>
              <div className="space-y-3 max-h-96 overflow-y-auto">
                {smashList.map(character => (
                  <div key={character.id} className="flex items-center space-x-3 p-3 bg-green-50 rounded-lg">
                    <img
                      src={character.imageUrl}
                      alt={character.name}
                      className="w-12 h-12 rounded-lg object-cover"
                      onError={(e) => {
                        e.target.style.display = 'none';
                      }}
                    />
                    <div className="flex-1">
                      <h3 className="font-semibold text-gray-800">{character.name}</h3>
                      <p className="text-sm text-gray-600">{character.description}</p>
                    </div>
                    <div className={`px-2 py-1 rounded text-xs font-bold text-white ${getElixirColor(character.elixir)}`}>
                      {character.elixir}
                    </div>
                  </div>
                ))}
                {smashList.length === 0 && (
                  <p className="text-gray-500 text-center py-8">No characters smashed 😢</p>
                )}
              </div>
            </div>

            {/* Pass List */}
            <div className="bg-white rounded-2xl p-6 shadow-2xl animate-slide-in">
              <h2 className="text-2xl font-bold text-red-600 mb-4 flex items-center">
                <span className="text-3xl mr-2">💔</span>
                Pass ({passList.length})
              </h2>
              <div className="space-y-3 max-h-96 overflow-y-auto">
                {passList.map(character => (
                  <div key={character.id} className="flex items-center space-x-3 p-3 bg-red-50 rounded-lg">
                    <img
                      src={character.imageUrl}
                      alt={character.name}
                      className="w-12 h-12 rounded-lg object-cover"
                      onError={(e) => {
                        e.target.style.display = 'none';
                      }}
                    />
                    <div className="flex-1">
                      <h3 className="font-semibold text-gray-800">{character.name}</h3>
                      <p className="text-sm text-gray-600">{character.description}</p>
                    </div>
                    <div className={`px-2 py-1 rounded text-xs font-bold text-white ${getElixirColor(character.elixir)}`}>
                      {character.elixir}
                    </div>
                  </div>
                ))}
                {passList.length === 0 && (
                  <p className="text-gray-500 text-center py-8">No characters passed 😍</p>
                )}
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }

  // Main Game View
  if (gameState === 'game') {
    const currentCharacter = characters[currentIndex];
    const progress = ((currentIndex + 1) / characters.length) * 100;

    return (
      <div className="min-h-screen bg-gradient-to-br from-indigo-900 via-purple-900 to-pink-900 relative overflow-hidden">
        {/* Animated Background Elements */}
        <div className="absolute inset-0">
          <div className="absolute top-20 left-10 w-72 h-72 bg-purple-500 rounded-full mix-blend-multiply filter blur-xl opacity-20 animate-pulse"></div>
          <div className="absolute top-40 right-10 w-72 h-72 bg-pink-500 rounded-full mix-blend-multiply filter blur-xl opacity-20 animate-pulse"></div>
          <div className="absolute -bottom-8 left-20 w-72 h-72 bg-indigo-500 rounded-full mix-blend-multiply filter blur-xl opacity-20 animate-pulse"></div>
        </div>

        <div className="relative z-10 p-4">
          <div className="max-w-4xl mx-auto">
            {/* Header */}
            <div className="text-center mb-8 animate-fade-in">
              <div className="flex justify-between items-center mb-6">
                <button
                  onClick={() => setGameState('landing')}
                  className="bg-black bg-opacity-50 text-white px-6 py-3 rounded-full font-bold hover:bg-opacity-70 transition-all duration-300 border border-gray-600 hover:border-white"
                >
                  ← Back to Menu
                </button>
                <div className="text-center">
                  <h1 className="text-2xl md:text-3xl font-black text-transparent bg-clip-text bg-gradient-to-r from-yellow-400 to-orange-500">
                    SMASH OR PASS
                  </h1>
                  <p className="text-white text-sm opacity-80">Clash Royale Edition</p>
                </div>
                <div className="w-32"></div> {/* Spacer */}
              </div>

              {/* Progress Bar */}
              <div className="bg-black bg-opacity-30 rounded-full h-4 mb-4 border border-gray-600">
                <div 
                  className="bg-gradient-to-r from-green-500 to-blue-500 h-4 rounded-full transition-all duration-500 ease-out shadow-lg"
                  style={{ width: `${progress}%` }}
                ></div>
              </div>

              <p className="text-white text-lg font-bold">
                {currentIndex + 1} of {characters.length}
              </p>
            </div>

            {/* Character Card */}
            <div className="group relative">
              <div className="absolute -inset-1 bg-gradient-to-r from-purple-600 to-pink-600 rounded-3xl blur opacity-75 group-hover:opacity-100 transition duration-1000 group-hover:duration-200 animate-pulse"></div>
              <div className="relative bg-black rounded-3xl p-8 border border-gray-800 shadow-2xl">
                <div className="text-center">
                  {/* Character Image */}
                  <div className="relative mb-8">
                    <div className="w-80 h-80 md:w-96 md:h-96 mx-auto rounded-3xl overflow-hidden shadow-2xl border-4 border-gray-700">
                      <img
                        src={currentCharacter.imageUrl}
                        alt={currentCharacter.name}
                        className="w-full h-full object-cover"
                      />
                    </div>

                    {/* Rarity Badge */}
                    <div className={`absolute top-4 right-4 px-4 py-2 rounded-full text-sm font-black text-white bg-black bg-opacity-80 backdrop-blur-sm border ${getRarityColor(currentCharacter.rarity)}`}>
                      {currentCharacter.rarity.toUpperCase()}
                    </div>

                    {/* Elixir Cost */}
                    <div className={`absolute top-4 left-4 w-12 h-12 rounded-full flex items-center justify-center text-white font-black text-lg shadow-lg ${getElixirColor(currentCharacter.elixir)}`}>
                      {currentCharacter.elixir}
                    </div>
                  </div>

                  {/* Character Info */}
                  <h2 className="text-4xl md:text-5xl font-black text-white mb-4 drop-shadow-lg">
                    {currentCharacter.name}
                  </h2>
                  <p className="text-gray-300 text-xl mb-10 max-w-2xl mx-auto leading-relaxed">
                    {currentCharacter.description}
                  </p>

                  {/* Action Buttons */}
                  <div className="flex flex-col sm:flex-row gap-6 justify-center">
                    <button
                      onClick={() => handleResponse('pass')}
                      className="flex-1 bg-gradient-to-r from-red-500 to-pink-500 hover:from-red-600 hover:to-pink-600 text-white font-black py-6 px-12 rounded-2xl text-2xl transition-all duration-300 transform hover:scale-110 hover:shadow-2xl shadow-lg border-2 border-red-400"
                    >
                      💔 PASS
                    </button>
                    <button
                      onClick={() => handleResponse('smash')}
                      className="flex-1 bg-gradient-to-r from-green-500 to-blue-500 hover:from-green-600 hover:to-blue-600 text-white font-black py-6 px-12 rounded-2xl text-2xl transition-all duration-300 transform hover:scale-110 hover:shadow-2xl shadow-lg border-2 border-green-400"
                    >
                      💚 SMASH
                    </button>
                  </div>
                </div>
              </div>
            </div>

            {/* Instructions */}
            <div className="text-center mt-8">
              <p className="text-white text-lg opacity-80 font-medium">
                Tap <span className="text-green-400 font-bold">SMASH</span> if you like this character, <span className="text-red-400 font-bold">PASS</span> if you don't!
              </p>
            </div>
          </div>
        </div>
      </div>
    );
  }

  // Default fallback (should never reach here)
  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-600 to-blue-600 p-4">
      <div className="text-center">
        <h1 className="text-white text-2xl">Loading...</h1>
      </div>
    </div>
  );
}

export default App;
